CREATE PROCEDURE [dbo].[AddDocumentType]
    @DocumentName NVARCHAR(100),
    @DocumentTypeID INT OUTPUT
AS

BEGIN
	INSERT dbo.request (
	[category_id],
	[subcategory_id],
	[request_type_id],
	[request_name],
	[request_status_id],
	[publish_date],
	[open_date],
	[contract_start_date],
	[contract_end_date]) OUTPUT INSERTED.request_id  
VALUES 
(
	@CategoryId,
	@SubCategoryId,
	@RequestTypeId,
	@RequestName,
	@RequestStatusId,
	@PublishDate,
	@OpenDate,
	@ContractStartDate,
	@ContractEndDate
)
END

