CREATE PROCEDURE [dbo].[CreateRequest]
	 
	@CategoryId INT,
	@SubCategoryId INT,
	@RequestTypeId INT,
	@RequestName INT,
	@RequestStatusId INT,
	@PublishDate DATETIME,
	@OpenDate DATETIME  ,
	@ContractStartDate  DATETIME ,
	@ContractEndDate  DATETIME 
AS

BEGIN
    -- SET NOCOUNT ON added to prevent extra result sets from
    -- interfering with SELECT statements.
    SET NOCOUNT ON;
	DECLARE @RowsAffected INT;
    -- Delete statement for the procedure
    DELETE FROM dbo.request
    WHERE request_id = @RequestId;

	SET @RowsAffected = @@ROWCOUNT;

    SELECT @RowsAffected;
END

BEGIN
	-- SET NOCOUNT ON added to prevent extra result sets from
	-- interfering with SELECT statements.
	SET NOCOUNT ON;

	SELECT * FROM dbo.request;
END

BEGIN
    SET NOCOUNT ON;

    INSERT INTO additional_document (request_id, document_name, document_description)

    VALUES (@RequestId, @DocumentName, @DocumentDescription);

	   SET @AdditionalDocumentID = SCOPE_IDENTITY();
END

CREATE PROCEDURE [dbo].[MunicipalityRequestDocument_Add]
	@RequestId INT,
    @DocumentName NVARCHAR(100),
	@DocumentDescription NVARCHAR(100) = NULL,
    @AdditionalDocumentID INT OUTPUT
AS

BEGIN
    SET NOCOUNT ON;
    DELETE FROM additional_document 
    WHERE request_id = @RequestId 
    AND additional_document_id = @MunicipalityRequestDocumentId;
END

CREATE PROCEDURE [dbo].[MunicipalityRequestDocument_Delete]
    @RequestId INT,
    @MunicipalityRequestDocumentId INT  
AS

BEGIN
    SET NOCOUNT ON;

    SELECT request_id, additional_document_id, document_name, document_description
    FROM Additional_Documents
    WHERE RequestId = @RequestId;
END

BEGIN
    SET NOCOUNT ON;

    -- Insert or update the required document information
    INSERT INTO request_requireddocument  (request_id, documenttype_id)
    VALUES (@RequestId, @DocumentTypeId);
END

CREATE PROCEDURE [dbo].[SaveRequiredDocuments]
    @RequestId INT,
    @DocumentTypeId INT
AS

BEGIN
    SET NOCOUNT ON;
	DECLARE @RowsAffected INT;
    -- Update the request status in the database
    UPDATE dbo.request
    SET request_status_id = @RequestStatusId
    WHERE request_id = @RequestId;

	SET @RowsAffected = @@ROWCOUNT;

    SELECT @RowsAffected;
END
